import React,{useState} from 'react'
import updateDoc from '../Actions'
import { useDispatch } from 'react-redux/es/hooks/useDispatch';
const Update = ({Name,ContactNumber}) => {
  const dispatch=useDispatch()
  const [Name1, setName] = useState(Name);
  const [ContactNumber1, setContactNumber] = useState(ContactNumber)
  return (
    <div>Update
    Name: <input onChange={(e)=>{setName(e.target.value)}} placeholder={Name1}/>
    ContactNumber: <input onChange={(e)=>{setContactNumber(e.target.value)}} placeholder={ContactNumber1}/>
    <button onClick={dispatch(updateDoc({Name1,ContactNumber1}))}>Update</button>
      {Name}
      {ContactNumber}
    </div>
  )
}

export default Update
