const initialList={
    list:[]
}

const crudReducer=(state=initialList,action)=>
{
    switch (action.type) {
        case 'ADD':
            const {name, contactNumber}=action.payload;
            console.log('==============FROM REDUCER======================');
            console.log(name, contactNumber);
            console.log('==============FROM REDUCER======================');
            return state
            break;
    
        default:
            return state;
            break;
    }
}

export default crudReducer;
